import React, { useState, useEffect } from 'react';
import {filteredOrders} from ''
import "./Orderpage.css";
import axios from 'axios';

function Orderpage() {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    new: true,
    packed: true,
    inTransit: true,
    delivered: true,
  });

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/orders');
      const data = response.data;
      setOrders(data);
      setFilteredOrders(data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, checked } = e.target;
    setFilterOptions((prevOptions) => ({
      ...prevOptions,
      [name]: checked,
    }));
  };

  useEffect(() => {
    const filtered = orders.filter((order) => {
      if (filterOptions.new && order.orderStatus === "New") {
        return true;
      }
      if (filterOptions.packed && order.orderStatus === "Packed") {
        return true;
      }
      if (filterOptions.inTransit && order.orderStatus === "InTransit") {
        return true;
      }
      if (filterOptions.delivered && order.orderStatus === "Delivered") {
        return true;
      }
      return false;
    });
    setFilteredOrders(filtered);
  }, [filterOptions, orders]);

  return (
    <div className="mainorderpage">
      <div className="leftorder">
        <div className="filter-options">
          <h3>Filter By Status</h3>
          <label>
            <input
              type="checkbox"
              name="new"
              checked={filterOptions.new}
              onChange={handleFilterChange}
            />
            New
          </label>
          <label>
            <input
              type="checkbox"
              name="packed"
              checked={filterOptions.packed}
              onChange={handleFilterChange}
            />
            Packed
          </label>
          <label>
            <input
              type="checkbox"
              name="inTransit"
              checked={filterOptions.inTransit}
              onChange={handleFilterChange}
            />
            In Transit
          </label>
          <label>
            <input
              type="checkbox"
              name="delivered"
              checked={filterOptions.delivered}
              onChange={handleFilterChange}
            />
            Delivered
          </label>
        </div>
      </div>
    </div>
  );
}

export default Orderpage;
