import React, { useState, useEffect } from 'react';
import "./Orderpage.css"

function Orderpage() {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    new: true,
    packed: true,
    inTransit: true,
    delivered: true,
  });
  const [count, setCount] = useState(0);

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filterOptions]); // Include filterOptions in the dependency array

  const fetchOrders = async () => {
    try {
      const response = await fetch(
        'https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/orders'
      );
      const data = await response.json();
      setOrders(data);
      setFilteredOrders(data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const applyFilters = () => {
    const filtered = orders.filter((order) => {
      if (filterOptions.new && order.orderStatus === 'New') {
        return true;
      }
      if (filterOptions.packed && order.orderStatus === 'Packed') {
        return true;
      }
      if (filterOptions.inTransit && order.orderStatus === 'InTransit') {
        return true;
      }
      if (filterOptions.delivered && order.orderStatus === 'Delivered') {
        return true;
      }
      return false;
    });
    setFilteredOrders(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, checked } = e.target;
    setFilterOptions((prevOptions) => {
      const updatedOptions = {
        ...prevOptions,
        [name]: checked,
      };

      // Update the count based on the checkbox status
      if (checked) {
        setCount((prevCount) => prevCount + 1);
      } else {
        setCount((prevCount) => prevCount - 1);
      }

      return updatedOptions;
    });
  };

  return (
    <div  className='main'>
      <h1>Order Page</h1>
      <div className='label-box'>
        <h3>Filters</h3>
        <p>
          Count: {count}
        </p>
        <label>
          <input className='inp-box'
            type="checkbox"
            name="new"
            checked={filterOptions.new}
            onChange={handleFilterChange}
          />
          New
        </label>
        <label>
          <input
            type="checkbox"
            name="packed"
            checked={filterOptions.packed}
            onChange={handleFilterChange}
          />
          Packed
        </label>
        <label>
          <input
            type="checkbox"
            name="inTransit"
            checked={filterOptions.inTransit}
            onChange={handleFilterChange}
          />
          In Transit
        </label>
        <label>
          <input
            type="checkbox"
            name="delivered"
            checked={filterOptions.delivered}
            onChange={handleFilterChange}
          />
          Delivered
        </label>
      </div>
      <ul>
        {filteredOrders.map((order) => (
          <li key={order.id}>{order.orderStatus}</li>
        ))}
      </ul>
    </div>
  );
}

export default Orderpage;
