import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Topbar.css';

function Topbar() {
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogout = () => {
    // Perform logout logic
    setIsLoggedIn(false);
  };

  const showLogoutButton = ['/order', '/product', '/user'].includes(location.pathname);

  return (
    <div className="main">
      <div className="leftmenu">
        <div className="logo">
          <Link to="/">
            <img
              className="imglogo"
              src="https://edu-web-fundamentals.web.app/static/media/logo.58169365.png"
              alt="kafenelogo"
            />
          </Link>
        </div>
      </div>
      <h1>Kafene</h1>
      <div className="rightmenu">
        <div className="menu-items">
          <Link to="/order" className="order">Order</Link>
          <Link to="/product" className="product">Products</Link>
          <Link to="/user" className="users">Users</Link>
        </div>
        {isLoggedIn ? (
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        ) : null}
      </div>
    </div>
  );
}

export default Topbar;
