import React, { useEffect, useState } from "react";
import "./Orderpage.css";

function Productpage() {
  const [orders, setOrders] = useState([]);
  const [filters, setFilters] = useState({
    Expired: true,
    LowStock: true,
  });
  const [filteredCount, setFilteredCount] = useState(0);

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    updateFilteredCount();
  }, [filters, orders, updateFilteredCount]);

  const fetchOrders = async () => {
    try {
      const response = await fetch(
        "https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/products"
      );
      const data = await response.json();
      setOrders(data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;
    setFilters((prevFilters) => ({ ...prevFilters, [name]: checked }));
  };

  const filteredOrders = orders.filter((order) => {
    if (filters.Expired && order.orderStatus === "Expired") {
      return true;
    }
    if (filters.LowStock && order.orderStatus === "Low Stock") {
      return true;
    }
    return false;
  });

  const updateFilteredCount = () => {
    const count = filteredOrders.length;
    setFilteredCount(count);
  };

  return (
    <div className="ordermain">
      <div className="left">
        <h1>Products</h1>
        <div className="filter">
          <h2>Filters</h2>
          <p>Count: {filteredCount}</p>
          <div className="check-iteam">
            <label>
              <input
                type="checkbox"
                name="Expired"
                checked={filters.Expired}
                onChange={handleCheckboxChange}
              />
              Expired
            </label>
            <label>
              <input
                type="checkbox"
                name="LowStock"
                checked={filters.LowStock}
                onChange={handleCheckboxChange}
              />
              Low Stock
            </label>
          </div>
        </div>
      </div>
      <div className="heding-name">
        <div className="name-hed">
          <p>ID</p>
          <p>Product Name</p>
          <p>Product Brand</p>
          <p>Expiry Date</p>
          <p>Unit Price</p>
          <p>Stock</p>
        </div>
        <div className="details" id="detailsContainer">
          {/* Display the filtered order list */}
          {filteredOrders.map((order) => (
            <div key={order.id}>
              <p className="id">{order.id}</p>
              <p className="cust">{`${order.medicineName} ${order.medicineBrand}`}</p>
              <p className="dta">{order.expiryDate}</p>
              <p className="amunt">{order.unitPrice}</p>
              <p className="ordstatus">{order.prescriptionRequired}</p>
              <p className="dta">{order.stock}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Productpage;
