import React, { useState } from "react";
import "./Orderpage.css";

function Orderpage() {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    new: true,
    packed: true,
    inTransit: true,
    delivered: true,
  });

  return (
    <div className="ordermain">
      <div className="left">
        <h1>Orders</h1>
        <div className="filter">
          <h2>Filters</h2>

          <p>
            {" "}
            Count:{" "}
            {filterOptions.new &&
              ` New (${
                filteredOrders.filter((order) => order.orderStatus === "New")
                  .length
              })`}
            {filterOptions.packed &&
              ` Packed (${
                filteredOrders.filter((order) => order.orderStatus === "Packed")
                  .length
              })`}
            {filterOptions.inTransit &&
              ` In Transit (${
                filteredOrders.filter(
                  (order) => order.orderStatus === "InTransit"
                ).length
              })`}
            {filterOptions.delivered &&
              ` Delivered (${
                filteredOrders.filter(
                  (order) => order.orderStatus === "Delivered"
                ).length
              })`}
          </p>
          <div className="check-iteam">
            <label>
              <input
                type="checkbox"
                name="new"
                checked={filterOptions.new}
                // onChange={handleFilterChange}
              />
              New
            </label>
            <label>
              <input
                type="checkbox"
                name="packed"
                checked={filterOptions.new}
                // onChange={handleFilterChange}
              />
              Packed
            </label>
            <label>
              <input
                type="checkbox"
                name="inTransit"
                checked={filterOptions.new}
                // onChange={handleFilterChange}
              />
              In Transit
            </label>
            <label>
              <input
                type="checkbox"
                name="delivered"
                checked={filterOptions.new}
                // onChange={handleFilterChange}
              />
              Delivered
            </label>
          </div>
        </div>
      </div>
      <div className="heding-name">
        <div className="name-hed">
          <p>Id</p>
          <p>Customer</p>
          <p>Date</p>
          <p>Amount</p>
          <p>Status</p>
        </div>
        <div className="details" id="detailsContainer">
          
        </div>
      </div>
    </div>
  );
}

export default Orderpage;

